-- Remove all the NPC's that are in the DB already to replace them

-- DELETE FROM `creature` WHERE `id` IN (34382,34383); // Still broken, seems some of them are not correctly set yet
-- DELETE FROM `game_event_creature` WHERE `event` = '35'; 

SET @guid := (SELECT MAX(guid) FROM `creature`);


SET @guid2 := (SELECT MAX(guid) FROM `creature`);
	
drop procedure if exists doWhile;
DELIMITER //  
CREATE PROCEDURE doWhile()   
BEGIN
DECLARE i INT DEFAULT @guid; 
WHILE (i <= @guid2) DO
    SET i = i+1;
END WHILE;
END;
//  

CALL doWhile(); 
drop procedure if exists doWhile;

-- Set the correct models / spells for the Ghostly and Cheerfulls


-- NPC Vendor & Catrina


SET @guid3 := (SELECT MAX(guid) FROM `creature`);
	
drop procedure if exists doWhile2;
DELIMITER //  
CREATE PROCEDURE doWhile2()   
BEGIN
WHILE (@guid2 <= @guid3) DO
    SET @guid2 = @guid2+1;
END WHILE;
END;
//  

CALL doWhile2(); 
drop procedure if exists doWhile2;

